'use strict';const inHtml=document.getElementById("main");fetch("http://localhost:3000/api/cameras").then(a=>{if(a.ok)return a.json();Promise.reject(a.status)}).then(a=>{a.forEach(b=>{inHtml.innerHTML+=`
            <div class="col-md-4">
                <div class="card mb-4 box-shadow">
                   <img class="card-img-top" src="${b.imageUrl}" alt="${b.name}">
                   <div class="card-body">
                     <h2>${b.name}</h2>
                     <p class="card-text">${b.description}</p>
                     <div class="d-flex justify-content-between align-items-center">
                        <div class="btn-group">
                            <button type="button" class="btn btn-sm btn-outline-secondary"><a href="produit.html?id=${b._id}" class=" text-center">Voir plus</a></button>
                            <button type="button" class="btn btn-sm btn-outline-secondary">${(b.price/100).toFixed(2)} \u20ac</button>
                        </div>
                        <small class="text-muted">9 mins</small>
                     </div>
                  </div>
                </div>
            </div>
                `})}).catch(a=>{console.log(a)});